import React, { useState } from 'react';
import productsArray from './state';

const AddProd = () => {
  const [productList, setProductList] = useState(productsArray);
  const [productName, setProductName] = useState('');

  const buttonAddClick = () => {
    const newProduct = {
      category: 'New Category',
      price: '$99.99',
      stocked: true,
      name: productName
    };

    const updatedProductList = [...productList, newProduct];
    setProductList(updatedProductList);
    setProductName('');

    alert(updatedProductList.length);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="AddProdName"
        value={productName}
        onChange={(event) => setProductName(event.target.value)}
      />
      <button
        style={{
          height: '45px',
          marginLeft: '10px'
        }}
        onClick={buttonAddClick}
      >
        confirm
      </button>
      {productList.map((product, index) => (
        <div key={index}>{product.name}</div>
      ))}
    </div>
  );
};

export default AddProd;
